"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

client/src/components/SpreadsheetGrid.ts.content:
import { useState, useRef } from "react";
import SpreadsheetCell from "./SpreadsheetCell";

interface CellData {
  address: string;
  value: string;
  backgroundColor?: string;
  color?: string; // Text color
  fontSize?: number;
  fontWeight?: string;
  fontFamily?: string;
  fontStyle?: string;
  textDecoration?: string;
  borderTop?: string;
  borderRight?: string;
  borderBottom?: string;
  borderLeft?: string;
}

interface MergedCell {
  startAddress: string;
  endAddress: string;
  colspan: number;
  rowspan: number;
}

interface SpreadsheetGridProps {
  rows?: number;
  cols?: number;
  selectedCells: string[];
  temporarySelectedCells?: string[];
  onCellSelect: (address: string) => void;
  onRowSelect: (rowIndex: number) => void;
  onColumnSelect: (colIndex: number) => void;
  cellData: Map<string, CellData>;
  onCellChange: (address: string, value: string) => void;
  onAddressChange?: (oldAddress: string, newAddress: string) => void;
  columnWidths?: Map<number, number>;
  rowHeights?: Map<number, number>;
  onColumnResize?: (colIndex: number, width: number) => void;
  onRowResize?: (rowIndex: number, height: number) => void;
  onDragSelection?: (addresses: string[]) => void;
  onPaste?: (
    startAddress: string, 
    data: string[][], 
    formatting?: Array<Array<{ 
      bold?: boolean; 
      italic?: boolean; 
      underline?: boolean;
      fontFamily?: string;
      fontSize?: number;
      color?: string;
      backgroundColor?: string;
    }>>,
    structuralInfo?: {
      mergedCells?: Array<{ rowIndex: number; colIndex: number; rowspan: number; colspan: number }>;
      rowHeights?: Array<{ rowIndex: number; height: number }>;
      colWidths?: Array<{ colIndex: number; width: number }>;
    }
  ) => void;
  mergedCells?: MergedCell[];
  onDeleteRow?: (rowIndex: number) => void;
  onInsertRow?: (rowIndex: number) => void;
  onDeleteColumn?: (colIndex: number) => void;
  onInsertColumn?: (colIndex: number) => void;
  defaultFormatting?: {
    fontSize?: number;
    fontWeight?: string;
    fontFamily?: string;
    fontStyle?: string;
    textDecoration?: string;
    backgroundColor?: string;
  };
}

export default function SpreadsheetGrid({
  rows = 20,
  cols = 10,
  selectedCells,
  temporarySelectedCells = [],
  onCellSelect,
  onRowSelect,
  onColumnSelect,
  cellData,
  onCellChange,
  onAddressChange,
  columnWidths = new Map(),
  rowHeights = new Map(),
  onColumnResize,
  onRowResize,
  onDragSelection,
  onPaste,
  mergedCells = [],
  onDeleteRow,
  onInsertRow,
  onDeleteColumn,
  onInsertColumn,
  defaultFormatting = {},
}: SpreadsheetGridProps) {
  const [resizingCol, setResizingCol] = useState<number | null>(null);
  const [resizingRow, setResizingRow] = useState<number | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState<string | null>(null);
  const startPosRef = useRef<{ x: number; y: number }>({ x: 0, y: 0 });

  const getColumnLabel = (index: number): string => {
    let label = "";
    let num = index;
    while (num >= 0) {
      label = String.fromCharCode(65 + (num % 26)) + label;
      num = Math.floor(num / 26) - 1;
    }
    return label;
  };

  const getCellAddress = (row: number, col: number): string => {
    return `${getColumnLabel(col)}${row + 1}`;
  };

  const getColumnIndexFromLabel = (label: string): number => {
    let index = 0;
    for (let i = 0; i < label.length; i++) {
      index = index * 26 + (label.charCodeAt(i) - 65 + 1);
    }
    return index - 1;
  };

  // Get row and column from address (Google Sheets style helper)
  const getRowColFromAddress = (address: string): { row: number; col: number } | null => {
    const rowMatch = address.match(/\d+/)?.[0];
    const colMatch = address.match(/^[A-Z]+/)?.[0];

    if (!rowMatch || !colMatch) return null;

    const row = parseInt(rowMatch) - 1;
    const col = getColumnIndexFromLabel(colMatch);

    return { row, col };
  };

  // Calculate selection boundaries (Google Sheets style)
  const getSelectionBoundaries = () => {
    const allSelected = [...selectedCells, ...temporarySelectedCells];
    if (allSelected.length === 0) return null;

    let minRow = Infinity;
    let maxRow = -Infinity;
    let minCol = Infinity;
    let maxCol = -Infinity;

    for (const addr of allSelected) {
      const pos = getRowColFromAddress(addr);
      if (!pos) continue;

      minRow = Math.min(minRow, pos.row);
      maxRow = Math.max(maxRow, pos.row);
      minCol = Math.min(minCol, pos.col);
      maxCol = Math.max(maxCol, pos.col);
    }

    return { minRow, maxRow, minCol, maxCol };
  };

  // Get first selected cell address
  const firstSelectedCell = selectedCells[0] || temporarySelectedCells[0] || null;

  // Check if column has any selected cells (Google Sheets style)
  const isColumnSelected = (colIndex: number): boolean => {
    const allSelected = [...selectedCells, ...temporarySelectedCells];
    return allSelected.some(addr => {
      const pos = getRowColFromAddress(addr);
      return pos?.col === colIndex;
    });
  };

  // Check if row has any selected cells (Google Sheets style)
  const isRowSelected = (rowIndex: number): boolean => {
    const allSelected = [...selectedCells, ...temporarySelectedCells];
    return allSelected.some(addr => {
      const pos = getRowColFromAddress(addr);
      return pos?.row === rowIndex;
    });
  };

  const handleColumnBorderMouseDown = (e: React.MouseEvent, colIndex: number) => {
    e.preventDefault();
    e.stopPropagation();
    setResizingCol(colIndex);
    startPosRef.current = { x: e.clientX, y: e.clientY };
  };

  const handleRowBorderMouseDown = (e: React.MouseEvent, rowIndex: number) => {
    e.preventDefault();
    e.stopPropagation();
    setResizingRow(rowIndex);
    startPosRef.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (resizingCol !== null && onColumnResize) {
      const deltaX = e.clientX - startPosRef.current.x;
      const currentWidth = columnWidths.get(resizingCol) || 32;
      const newWidth = Math.max(32, currentWidth + deltaX);
      onColumnResize(resizingCol, newWidth);
      startPosRef.current = { x: e.clientX, y: e.clientY };
    } else if (resizingRow !== null && onRowResize) {
      const deltaY = e.clientY - startPosRef.current.y;
      const currentHeight = rowHeights.get(resizingRow) || 10.5;
      const newHeight = Math.max(10.5, currentHeight + deltaY);
      onRowResize(resizingRow, newHeight);
      startPosRef.current = { x: e.clientX, y: e.clientY };
    }
  };

  const handleMouseUp = () => {
    setResizingCol(null);
    setResizingRow(null);
    // Also end dragging when mouse is released anywhere
    if (isDragging) {
      setIsDragging(false);
      setDragStart(null);
    }
  };

  const handleCellMouseDown = (address: string) => {
    setIsDragging(true);
    setDragStart(address);
  };

  const handleCellMouseEnter = (address: string) => {
    if (isDragging && dragStart && onDragSelection) {
      const startRowMatch = dragStart.match(/\d+/)?.[0];
      const startColMatch = dragStart.match(/^[A-Z]+/)?.[0];
      const endRowMatch = address.match(/\d+/)?.[0];
      const endColMatch = address.match(/^[A-Z]+/)?.[0];

      if (!startRowMatch || !startColMatch || !endRowMatch || !endColMatch) return;

      const startRow = parseInt(startRowMatch) - 1;
      const startCol = getColumnIndexFromLabel(startColMatch);
      const endRow = parseInt(endRowMatch) - 1;
      const endCol = getColumnIndexFromLabel(endColMatch);

      const minRow = Math.min(startRow, endRow);
      const maxRow = Math.max(startRow, endRow);
      const minCol = Math.min(startCol, endCol);
      const maxCol = Math.max(startCol, endCol);

      const addresses: string[] = [];
      for (let r = minRow; r <= maxRow; r++) {
        for (let c = minCol; c <= maxCol; c++) {
          addresses.push(getCellAddress(r, c));
        }
      }
      onDragSelection(addresses);
    }
  };

  const handleCellMouseUp = () => {
    setIsDragging(false);
    setDragStart(null);
  };

  const getCellRowCol = (addr: string) => {
    const match = addr.match(/^([A-Z]+)(\d+)/);
    if (!match) return { row: 0, col: 0 };
    const colLabel = match[1];
    const row = parseInt(match[2]) - 1;
    let col = 0;
    for (let i = 0; i < colLabel.length; i++) {
      col = col * 26 + (colLabel.charCodeAt(i) - 65 + 1);
    }
    return { row, col: col - 1 };
  };

  const getMergedCellInfo = (address: string) => {
    const merged = mergedCells.find(m => {
      const start = getCellRowCol(m.startAddress);
      const current = getCellRowCol(address);
      return current.row === start.row && current.col === start.col;
    });
    if (merged) return merged;

    const hiddenIn = mergedCells.find(m => {
      const start = getCellRowCol(m.startAddress);
      const current = getCellRowCol(address);

      return current.row >= start.row && 
             current.row < start.row + m.rowspan &&
             current.col >= start.col && 
             current.col < start.col + m.colspan &&
             !(current.row === start.row && current.col === start.col);
    });

    return hiddenIn ? { ...hiddenIn, isHidden: true } : null;
  };

  return (
    <div 
      className="h-full w-full overflow-auto custom-scrollbar"
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp}
    >
      <div className="p-0">
        <table className="border-collapse border border-border" style={{ tableLayout: 'fixed', width: 'max-content', minWidth: '100%', lineHeight: '1', position: 'relative' }}>
          <colgroup>
            <col style={{ width: '48px' }} />
            {Array.from({ length: cols }).map((_, colIndex) => {
              const width = columnWidths.get(colIndex) || 100; // Google Sheets default
              return <col key={colIndex} style={{ width: `${width}px` }} />;
            })}
          </colgroup>
          <thead>
            <tr>
              <th 
                className="bg-card border-r border-b border-border" 
                style={{
                  position: 'sticky',
                  left: 0,
                  top: 0,
                  zIndex: 20,
                  backgroundColor: 'hsl(var(--card))'
                }}
              />
              {Array.from({ length: cols }).map((_, colIndex) => {
                const colSelected = isColumnSelected(colIndex);
                return (
                <th
                  key={colIndex}
                  className="relative bg-card border-r border-b border-border font-semibold text-sm hover-elevate cursor-pointer p-0 m-0"
                  style={{ 
                    height: '24px', 
                    lineHeight: '1',
                    position: 'sticky',
                    top: 0,
                    zIndex: 15,
                    backgroundColor: colSelected ? 'var(--sheets-header-bg)' : 'hsl(var(--card))',
                  }}
                  onClick={() => onColumnSelect(colIndex)}
                  data-testid={`header-col-${getColumnLabel(colIndex)}`}
                >
                  {getColumnLabel(colIndex)}
                  {/* Delete Column Button */}
                  {onDeleteColumn && (
                    <button
                      className="absolute left-0 top-0 w-3 h-3 text-[8px] flex items-center justify-center bg-muted/40 text-muted-foreground opacity-60 hover:opacity-100 hover:bg-red-500 hover:text-white transition-all z-20"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteColumn(colIndex);
                      }}
                      title="Delete column"
                      data-testid={`button-delete-col-${colIndex}`}
                    >
                      √ó
                    </button>
                  )}
                  {/* Insert Column Button */}
                  {onInsertColumn && (
                    <button
                      className="absolute right-0 top-0 w-3 h-3 text-[8px] flex items-center justify-center bg-muted/40 text-muted-foreground opacity-60 hover:opacity-100 hover:bg-green-500 hover:text-white transition-all z-20"
                      onClick={(e) => {
                        e.stopPropagation();
                        onInsertColumn(colIndex);
                      }}
                      title="Insert column"
                      data-testid={`button-insert-col-${colIndex}`}
                    >
                      +
                    </button>
                  )}
                  <div
                    className="absolute right-0 top-0 h-full w-2 cursor-col-resize hover:bg-primary z-10 transition-colors"
                    onMouseDown={(e) => handleColumnBorderMouseDown(e, colIndex)}
                  />
                </th>
                )
              })}
            </tr>
          </thead>
          <tbody>
            {Array.from({ length: rows }).map((_, rowIndex) => {
              const height = rowHeights.get(rowIndex) || 21; // Google Sheets default
              const rowSelected = isRowSelected(rowIndex);
              return (
                <tr key={rowIndex} style={{ height: `${height}px`, maxHeight: `${height}px` }}>
                  <th
                    className="relative bg-card border-r border-b border-border text-sm font-medium sticky left-0 z-10 hover-elevate cursor-pointer p-0 m-0"
                    style={{ 
                      height: `${height}px`, 
                      lineHeight: '1',
                      backgroundColor: rowSelected ? 'var(--sheets-header-bg)' : undefined,
                    }}
                    onClick={() => onRowSelect(rowIndex)}
                    data-testid={`header-row-${rowIndex + 1}`}
                  >
                    {rowIndex + 1}
                    {/* Delete Row Button */}
                    {onDeleteRow && (
                      <button
                        className="absolute left-0 top-0 w-3 h-3 text-[8px] flex items-center justify-center bg-muted/40 text-muted-foreground opacity-60 hover:opacity-100 hover:bg-red-500 hover:text-white transition-all z-20"
                        onClick={(e) => {
                          e.stopPropagation();
                          onDeleteRow(rowIndex);
                        }}
                        title="Delete row"
                        data-testid={`button-delete-row-${rowIndex}`}
                      >
                        √ó
                      </button>
                    )}
                    {/* Insert Row Button */}
                    {onInsertRow && (
                      <button
                        className="absolute right-0 top-0 w-3 h-3 text-[8px] flex items-center justify-center bg-muted/40 text-muted-foreground opacity-60 hover:opacity-100 hover:bg-green-500 hover:text-white transition-all z-20"
                        onClick={(e) => {
                          e.stopPropagation();
                          onInsertRow(rowIndex);
                        }}
                        title="Insert row"
                        data-testid={`button-insert-row-${rowIndex}`}
                      >
                        +
                      </button>
                    )}
                    <div
                      className="absolute bottom-0 left-0 w-full h-2 cursor-row-resize hover:bg-primary z-10 transition-colors"
                      onMouseDown={(e) => handleRowBorderMouseDown(e, rowIndex)}
                    />
                  </th>
                  {Array.from({ length: cols }).map((_, colIndex) => {
                    const address = getCellAddress(rowIndex, colIndex);
                    const mergeInfo = getMergedCellInfo(address);

                    if (mergeInfo && (mergeInfo as any).isHidden) {
                      return null;
                    }

                    let cell = cellData.get(address);

                    if (!cell && mergeInfo && !((mergeInfo as any).isHidden)) {
                      cell = cellData.get(mergeInfo.startAddress);
                    }

                    if (!cell) {
                      cell = {
                        address,
                        value: "",
                        // Don't set defaults here - let them come from defaultFormatting prop
                      };
                    }

                    let displayAddress = cell.address;
                    if (mergeInfo && !((mergeInfo as any).isHidden) && (mergeInfo.colspan > 1 || mergeInfo.rowspan > 1)) {
                      displayAddress = `${mergeInfo.startAddress}:${mergeInfo.endAddress}`;
                    }

                    const isSelected = selectedCells.includes(address) || selectedCells.includes(cell.address);
                    const isTemporary = temporarySelectedCells.includes(address);

                    // Google Sheets style selection
                    const isFirstSelected = firstSelectedCell === address || firstSelectedCell === cell.address;
                    const boundaries = getSelectionBoundaries();
                    const currentPos = getRowColFromAddress(address);

                    let isInSelectionBoundary = {
                      top: false,
                      right: false,
                      bottom: false,
                      left: false,
                    };

                    if (boundaries && currentPos && (isSelected || isTemporary)) {
                      isInSelectionBoundary = {
                        top: currentPos.row === boundaries.minRow,
                        right: currentPos.col === boundaries.maxCol,
                        bottom: currentPos.row === boundaries.maxRow,
                        left: currentPos.col === boundaries.minCol,
                      };
                    }

                    const colspan = mergeInfo && !((mergeInfo as any).isHidden) ? mergeInfo.colspan : 1;
                    const rowspan = mergeInfo && !((mergeInfo as any).isHidden) ? mergeInfo.rowspan : 1;

                    let cellHeight = rowHeights.get(rowIndex) || 21;
                    if (rowspan > 1) {
                      cellHeight = 0;
                      for (let i = 0; i < rowspan; i++) {
                        cellHeight += rowHeights.get(rowIndex + i) || 21;
                      }
                    }

                    return (
                      <td
                        key={address}
                        colSpan={colspan}
                        rowSpan={rowspan}
                        className="border border-border p-0 m-0 relative"
                        style={{ height: `${cellHeight}px`, lineHeight: '1' }}
                        onMouseDown={() => handleCellMouseDown(address)}
                        onMouseEnter={() => handleCellMouseEnter(address)}
                        onMouseUp={handleCellMouseUp}
                      >
                        <SpreadsheetCell
                          address={displayAddress}
                          value={cell.value}
                          isSelected={isSelected}
                          isTemporary={isTemporary}
                          isFirstSelected={isFirstSelected}
                          isInSelectionBoundary={isInSelectionBoundary}
                          isDragging={isDragging}
                          backgroundColor={cell.backgroundColor ?? defaultFormatting.backgroundColor}
                          color={cell.color}
                          fontSize={cell.fontSize ?? defaultFormatting.fontSize}
                          fontWeight={cell.fontWeight ?? defaultFormatting.fontWeight}
                          fontFamily={cell.fontFamily ?? defaultFormatting.fontFamily}
                          fontStyle={cell.fontStyle ?? defaultFormatting.fontStyle}
                          textDecoration={cell.textDecoration ?? defaultFormatting.textDecoration}
                          borderTop={cell.borderTop}
                          borderRight={cell.borderRight}
                          borderBottom={cell.borderBottom}
                          borderLeft={cell.borderLeft}
                          cellHeight={cellHeight}
                          onClick={() => onCellSelect(cell.address)}
                          onDoubleClick={() => onCellSelect(cell.address)}
                          onChange={(value) => onCellChange(cell.address, value)}
                          onAddressChange={(newAddr) => onAddressChange?.(cell.address, newAddr)}
                          onPaste={onPaste}
                        />
                      </td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

