import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Bold, Italic, Underline } from "lucide-react";

interface ExcelFontControlsProps {
  onFontSizeChange: (size: number) => void;
  onFontWeightChange: (weight: string) => void;
  onFontFamilyChange: (family: string) => void;
  onItalicToggle: () => void;
  onUnderlineToggle: () => void;
  currentFontSize?: number;
  currentFontWeight?: string;
  currentFontFamily?: string;
  currentFontStyle?: string;
  currentTextDecoration?: string;
}

const FONT_SIZES = [8, 9, 10, 11, 12, 13, 14, 16, 18, 20, 22, 24, 26, 28, 36, 48, 72];
const FONT_FAMILIES = ["Arial", "Calibri", "Times New Roman", "Verdana", "Georgia"]; // Arial first (Google Sheets default)

export default function ExcelFontControls({
  onFontSizeChange,
  onFontWeightChange,
  onFontFamilyChange,
  onItalicToggle,
  onUnderlineToggle,
  currentFontSize = 10, // Google Sheets default
  currentFontWeight = "normal",
  currentFontFamily = "Arial", // Google Sheets default
  currentFontStyle = "normal",
  currentTextDecoration = "none",
}: ExcelFontControlsProps) {
  const isBold = currentFontWeight === "bold" || currentFontWeight === "700";
  const isItalic = currentFontStyle === "italic";
  const isUnderline = currentTextDecoration === "underline";

  const handleBoldToggle = () => {
    onFontWeightChange(isBold ? "normal" : "bold");
  };

  const handleFontFamilyChange = (family: string) => {
    onFontFamilyChange(family);
  };

  return (
    <div className="flex items-center gap-1 bg-muted/30 px-2 py-1 rounded-md border border-border select-none">
      <Select value={currentFontFamily} onValueChange={handleFontFamilyChange}>
        <SelectTrigger className="w-28 h-7 text-xs border-none bg-transparent" data-testid="select-font-family">
          <SelectValue />
        </SelectTrigger>
        <SelectContent>
          {FONT_FAMILIES.map((font) => (
            <SelectItem key={font} value={font}>
              {font}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>

      <div className="h-4 w-px bg-border" />

      <Select
        value={currentFontSize.toString()}
        onValueChange={(value) => onFontSizeChange(parseInt(value))}
      >
        <SelectTrigger className="w-14 h-7 text-xs border-none bg-transparent" data-testid="select-font-size">
          <SelectValue />
        </SelectTrigger>
        <SelectContent>
          {FONT_SIZES.map((size) => (
            <SelectItem key={size} value={size.toString()}>
              {size}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>

      <div className="h-4 w-px bg-border" />

      <Button
        variant={isBold ? "default" : "ghost"}
        size="icon"
        className="h-7 w-7"
        onClick={handleBoldToggle}
        data-testid="button-bold"
      >
        <Bold className="h-3.5 w-3.5" />
      </Button>

      <Button
        variant={isItalic ? "default" : "ghost"}
        size="icon"
        className="h-7 w-7"
        onClick={onItalicToggle}
        data-testid="button-italic"
      >
        <Italic className="h-3.5 w-3.5" />
      </Button>

      <Button
        variant={isUnderline ? "default" : "ghost"}
        size="icon"
        className="h-7 w-7"
        onClick={onUnderlineToggle}
        data-testid="button-underline"
      >
        <Underline className="h-3.5 w-3.5" />
      </Button>
    </div>
  );
}

client/src/components/SheetTabs.tsx.content:
import { Plus, Menu } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface Sheet {
  id: string;
  name: string;
}

interface SheetTabsProps {
  sheets: Sheet[];
  activeSheetId: string;
  onSheetChange: (sheetId: string) => void;
  onAddSheet: () => void;
  onRenameSheet: (sheetId: string, newName: string) => void;
  onDeleteSheet: (sheetId: string) => void;
}

export default function SheetTabs({
  sheets,
  activeSheetId,
  onSheetChange,
  onAddSheet,
  onRenameSheet,
  onDeleteSheet,
}: SheetTabsProps) {
  return (
    <div className="flex items-center gap-1 h-10 border-t border-border bg-background px-2">
      {/* Add Sheet Button */}
      <Button
        size="icon"
        variant="ghost"
        className="h-7 w-7"
        onClick={onAddSheet}
        data-testid="button-add-sheet"
        title="Add sheet"
      >
        <Plus className="h-4 w-4" />
      </Button>

      {/* All Sheets Menu */}
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            size="icon"
            variant="ghost"
            className="h-7 w-7"
            data-testid="button-all-sheets"
            title="All sheets"
          >
            <Menu className="h-4 w-4" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="start">
          {sheets.map((sheet) => (
            <DropdownMenuItem
              key={sheet.id}
              onClick={() => onSheetChange(sheet.id)}
              className={activeSheetId === sheet.id ? "bg-accent" : ""}
              data-testid={`menu-sheet-${sheet.id}`}
            >
              {sheet.name}
            </DropdownMenuItem>
          ))}
        </DropdownMenuContent>
      </DropdownMenu>

      {/* Sheet Tabs */}
      <div className="flex items-center gap-1 overflow-x-auto flex-1">
        {sheets.map((sheet) => (
          <div
            key={sheet.id}
            className={`
              px-3 py-1 text-sm cursor-pointer rounded-t-md select-none
              transition-colors whitespace-nowrap
              ${
                activeSheetId === sheet.id
                  ? "bg-background border-t border-l border-r border-border font-medium"
                  : "hover-elevate text-muted-foreground"
              }
            `}
            onClick={() => onSheetChange(sheet.id)}
            onDoubleClick={() => {
              const newName = prompt("Rename sheet:", sheet.name);
              if (newName && newName.trim()) {
                onRenameSheet(sheet.id, newName.trim());
              }
            }}
            onContextMenu={(e) => {
              e.preventDefault();
              if (sheets.length > 1) {
                if (confirm(`Delete "${sheet.name}"?`)) {
                  onDeleteSheet(sheet.id);
                }
              }
            }}
            data-testid={`tab-sheet-${sheet.id}`}
            title={`${sheet.name} (Double-click to rename, Right-click to delete)`}
          >
            {sheet.name}
          </div>
        ))}
      </div>
    </div>
  );
}

